    private void txtUserNameKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtUserNameKeyPressed
        // TODO add your handling code here:
        char c = evt.getKeyChar();
        if(Character.isWhitespace(c)||Character.isLetterOrDigit(c) ||Character.isISOControl(c))
        {
            txtUserName.setEditable(true);
        }
        else
        {
            txtUserName.setEditable(false);
            JOptionPane.showMessageDialog(this, "No special characters allowed!");
        }
    }//GEN-LAST:event_txtUserNameKeyPressed

    private void addButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addButtonActionPerformed
        // TODO add your handling code here:
        String username = txtUserName.getText();
        String password = txtPassword.getText();

        if(username.equals("sys") || password.equals("sys"))
        {
            JOptionPane.showMessageDialog(null, "login successful");
            SystemAdminFrame systemAdmin = new SystemAdminFrame();
            systemAdmin.setVisible(true);
        }
        else
        {
            JOptionPane.showMessageDialog(null, "login unsuccessful \nincorrect details");
        }
        txtUserName.setText("");
        txtPassword.setText("");
    }//GEN-LAST:event_addButtonActionPerformed

    private void resetButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_resetButtonActionPerformed
        // TODO add your handling code here:
        txtUserName.setText("");
        txtPassword.setText("");

        JOptionPane.showMessageDialog(null, "feilds set to reset");
    }//GEN-LAST:event_resetButtonActionPerformed

